import {Col, Container, Row} from "reactstrap";
import personImage from '../asset/bannerBottomImage.png';
import "../style/fonts.css";

export function HomeComponentSecOne() {
    const styles = {
        customFont: {fontFamily: "DiodrumMedium", marginTop: "3rem"},
        cardBase: {
            backgroundColor: "rgb(78,10,171)",
            color: "white",
            width: "125px",
            height: "100px",
            textAlign: "center",
            display: "flex",
            flexDirection: "column", // Aligns items vertically
            justifyContent: "center",
            alignItems: "center",
            marginBottom: '15px',
        },
        cardLeft: {borderTopLeftRadius: "15px", borderBottomLeftRadius: "15px"},
        cardRight: {borderTopRightRadius: "15px", borderBottomRightRadius: "15px"},
        cardHeading: {
            paddingTop: "13px",
            fontSize: "33px",
            fontFamily: "DiodrumBold"
        },
        cardSubHeading: {
            marginTop: "-13px",
            fontSize: "26px",
            fontFamily: "DiodrumRegular"
        },
        paragraph: {
            color: "rgb(96,96,96)",
            marginTop: "30px",
            textAlign: "right",
            fontSize: '36px'
        },
        heading: {
            color: "rgb(80,177,177)",
            fontFamily: "DiodrumBold",
            textAlign: "right",
            fontSize: "50px",
        },
        subHeading: {
            fontFamily: "DiodrumBold",
            textAlign: "right",
            fontSize: "70px",
            color: "rgb(12,12,150)",
        },
        image: {
            height: "auto"
        },
    };

    return (
        <Container style={styles.customFont}>
            <Row className="d-flex justify-content-between">
                <Col xl={6} md={6} sm={12} className="mb-3">
                    <img src={personImage} className="img-fluid" alt="Person" style={styles.image}/>
                </Col>
                <Col xl={6} md={6} sm={12} style={{textAlign: "right"}}>
                    <div className="d-inline-flex flex-wrap justify-content-between gap-4">
                        {['ﺍﻻﻳﺎﻡ', 'ﺍﻟﺴﺎﻋﺎﺕ', 'ﺍﻟﺪﻗﺎﺋﻖ', 'ﺍﻟﺜﻮﺍﻧﻲ'].map((text, index) => (
                            <div
                                key={index}
                                style={{
                                    ...styles.cardBase,
                                    ...(index === 0 ? styles.cardLeft : index === 3 ? styles.cardRight : {})
                                }}
                            >
                                <h6 style={styles.cardHeading}>00</h6>
                                <p style={styles.cardSubHeading}>{text}</p>
                            </div>
                        ))}
                    </div>
                    <div>
                        <p style={styles.paragraph}>
                            ﺑﺮﻋﺎﻳﺔ ﻣﻌﺎﻟﻲ ﻭﺯﻳﺮ ﺍﻟﻌﺪﻝ ﺭﺋﻴﺲ ﻣﺠﻠﺲ
                            ﺇﺩﺍﺭﺓ ﺍﻟﻬﻴﺌــــﺔ ﺍﻟﺴﻌـــﻮﺩﻳﺔ ﻟﻠﻤﺤﺎﻣﻴـــﻦ
                        </p>
                        <p style={styles.heading}>ﺩ. ﻭﻟﻴﺪ ﺑﻦ ﻣﺤﻤﺪ ﺍﻟﺼﻤﻌﺎﻧﻲ</p>
                        <p style={styles.subHeading}>ﻋـﻦ ﺍﻟﻤﺆﺗﻤـﺮ</p>
                    </div>
                </Col>
            </Row>
        </Container>
    );
}
